def on_on_overlap(sprite, otherSprite):
    odpad.destroy()
    sprite.destroy()
    info.change_life_by(-1)
    if info.life() < 1:
        game.over(False)
sprites.on_overlap(SpriteKind.projectile, SpriteKind.player, on_on_overlap)

